// Enhanced processing and validation
const agentOutput = items[0].json;

let postsData;
try {
  // Parse agent output
  if (typeof agentOutput.output === 'string') {
    postsData = JSON.parse(agentOutput.output);
  } else {
    postsData = agentOutput.output || agentOutput;
  }
} catch (error) {
  // Fallback: try to extract from text
  const textOutput = agentOutput.output || JSON.stringify(agentOutput);
  const jsonMatch = textOutput.match(/\{[\s\S]*\}/);
  if (jsonMatch) {
    postsData = JSON.parse(jsonMatch[0]);
  } else {
    throw new Error('Could not parse agent output');
  }
}

const posts = postsData.posts || [];

// Format posts for script generation
const formattedPosts = posts.map((post, index) => ({
  ranking: index + 1,
  title: post.title,
  score: post.score || 0,
  comments: post.comments || 0
}));

// Create text summary for AI
const postsText = formattedPosts.map(post => {
  return `${post.ranking}. \"${post.title}\" (${post.score} upvotes)`;
}).join('\\n');

return [{
  json: {
    trendingPosts: formattedPosts,
    postsText: postsText,
    metadata: {
      agentUsed: true,
      sourcesFetched: postsData.sources_fetched || []
    }
  }
}];

