// Prepare VEO3 prompt from script visual cues
const script = $json.script || '';
const title = $json.title || '';

// Extract visual cues from script
const visualCues = [];
const visualRegex = /\[VISUAL:\s*([^\]]+)\]/gi;
let match;

while ((match = visualRegex.exec(script)) !== null) {
  visualCues.push(match[1].trim());
}

// Create VEO3 prompt combining all visual elements
const veoPrompt = visualCues.length > 0 
  ? visualCues.join('. ') + '. Professional educational tech style, vibrant colors, smooth animations, 9:16 vertical format.'
  : `${title}. Professional educational tech animation explaining the concept. Vibrant colors, smooth transitions, modern minimalist style. 9:16 vertical format.`;

return [{
  json: {
    ...items[0].json,
    veoPrompt: veoPrompt,
    visualCues: visualCues,
    duration: '120s'
  }
}];

