// Parse VEO3 response
const response = items[0].json;

if (!response.predictions || response.predictions.length === 0) {
  throw new Error('VEO3 did not return any predictions');
}

const prediction = response.predictions[0];
const videoUri = prediction.videoUri || prediction.gcsUri;

if (!videoUri) {
  throw new Error('No video URI in VEO3 response');
}

// Convert GCS URL to downloadable URL
let downloadUrl = videoUri;
if (videoUri.startsWith('gs://')) {
  downloadUrl = videoUri.replace('gs://', 'https://storage.googleapis.com/');
}

return [{
  json: {
    videoUri: videoUri,
    downloadUrl: downloadUrl,
    videoId: $('Prepare VEO3 Prompt').first().json.videoId,
    veoPrompt: $('Prepare VEO3 Prompt').first().json.veoPrompt
  }
}];

const scriptData = $('Check Voice Approval').first().json;
const thumbnailResponse = $('Generate Thumbnail').first().json;
const thumbnailUrl = thumbnailResponse.data?.[0]?.url;

return [{
  json: {
    ...scriptData,
    thumbnailUrl: thumbnailUrl,
    videoSource: 'VEO3_AI'
  }
}];

const fs = require('fs');
const videoPath = `C:\\temp\\final_${items[0].json.videoId}.mp4`;

// Check video file exists
if (!fs.existsSync(videoPath)) {
  throw new Error('Video file not created!');
}

const stats = fs.statSync(videoPath);
if (stats.size < 100000) {
  throw new Error('Video file too small - creation likely failed');
}

// Parse AI validation
const aiResponse = items[0].json;
let content = aiResponse.choices?.[0]?.message?.content;
content = content.replace(/```json\n?/g, '').replace(/```\n?/g, '').trim();
const finalReview = JSON.parse(content);

if (!finalReview.approved || !finalReview.professionalQuality || finalReview.score < 85) {
  throw new Error(`FINAL AI QUALITY CHECK FAILED (Score: ${finalReview.score}/100):\nProfessional Quality: ${finalReview.professionalQuality}\nIssues: ${finalReview.issues.join(', ')}\nRecommendation: ${finalReview.recommendation}`);
}

const scriptData = $('Create Captions').first().json;

return [{
  json: {
    ...scriptData,
    finalVideoPath: videoPath,
    thumbnailPath: `C:\\temp\\thumb_${scriptData.videoId}.png`,
    fileSize: stats.size,
    fileSizeMB: (stats.size / 1024 / 1024).toFixed(2),
    finalAiReview: finalReview,
    status: 'âœ… PROFESSIONAL VIDEO READY',
    completedAt: new Date().toISOString(),
    qualityScores: {
      scriptQuality: scriptData.aiValidation.score,
      voiceQuality: scriptData.voiceValidation.score,
      videoQuality: finalReview.score,
      overallScore: Math.round((scriptData.aiValidation.score + scriptData.voiceValidation.score + finalReview.score) / 3)
    }
  }
}];
